mapper.delete:
delete from BOOKS
where BOOKS_DATE BETWEEN #{start} AND #{end}
AND BOOKS_TYPE = #{booksType}
AND USER_ID = #{userId}

mapper.select:
select B.BOOKS_ID -- 家計簿ID , B.BOOKS_TYPE -- 帳簿の種類(収入、支出を選ぶ) , B.BOOKS_DATE -- 日付(収入日、購入日) , B.BOOKS_PLACE -- 場所(収入元、購入先) , B.CAT_CODE -- カテゴリーコード , C.CAT_NAME -- カテゴリー名 , C.IMG_ID -- 画像ID , I.IMG_PATH --画像パス , I.IMG_NAME --画像名 , B.BOOKS_METHOD -- 方法(受け取り方、支払い方) , B.BOOKS_AMMOUNT -- 金額 , B.INS_DATE -- 登録日時 , B.INS_USER -- 登録ユーザー , B.UPD_DATE -- 更新日時 , B.UPD_USER -- 更新ユーザー from BOOKS B left outer join CATEGORY C on B.CAT_CODE = C.CAT_CODE left outer join IMAGE I on C.IMG_ID = I.IMG_ID
B.BOOKS_DATE BETWEEN #{start} AND #{end}
and B.BOOKS_TYPE = #{booksType}
AND USER_ID = #{userId}
order by BOOKS_DATE
